page_settings.eael_custom_js:
    $('.dsb-btn').prop('disabled', true);
    $(".mask-cel-ddd").mask("(00) 00000-0000");

    $("#form-novamente").on('click', function () {
        $(".modal-enviado").hide();
        $(".modal-enviando").hide();
        $(".box-espera").hide();
        $(".modal-formulario").show();
    });

    $("#enviaWhats").on('click', function () {
        $(".modal-formulario").hide();
        $(".box-espera").show();
        $(".modal-enviando").show();

        var timeleft = 4;
        var downloadTimer = setInterval(function () {
            document.getElementById("countdown").innerHTML = timeleft;

            timeleft -= 1;

            if (timeleft === 0) {
                document.getElementById("segundos").innerHTML = 'segundo';
            }

            if (timeleft <= -1) {
                $(".modal-enviando").hide();
                $(".modal-enviado").show();
            }
        }, 1000);
    });

